INSERT INTO `vcr_curriculum_exercise` VALUES ('20', '24', '3', '43', 'A：问女士年纪是不太礼貌的。', null, '/static/update/admin_audio/D08EF8E5A5D04B8787CE7A34D8EE6780.mp3', 'A:  It’s not polite to ask a woman her age.', '0', '2018-10-25 17:51:34');
