INSERT INTO `vcr_curriculum_exercise` VALUES ('27', '24', '3', '43', 'A: 哦，真棒！', null, '/static/update/admin_audio/EF0EF485415344BFAC7E190250FE5CEB.mp3', 'A：Oh great!', '0', '2018-10-25 17:51:34');
INSERT INTO `vcr_curriculum_exercise` VALUES ('28', '24', '3', '43', 'B：我去欧洲旅游了。', null, '/static/update/admin_audio/567AD8DBB41C465EBDA0BA19AAE0BF6F.mp3', 'B：I travelled to Europe. ', '0', '2018-10-25 17:51:34');
