INSERT INTO `vcr_curriculum_exercise` VALUES ('58', '25', '7', '47', 'B：我也很喜欢成都本地的茉莉花茶。下课后你们通常做什么？', null, '/static/update/admin_audio/CD72B710792E43299EAFC565690188A1.mp3', 'B：I do love the local jasmine tea. What do you usually do after class?', '0', '2018-10-25 17:51:35');
INSERT INTO `vcr_curriculum_exercise` VALUES ('59', '25', '7', '47', 'A：噢，成都人一般习惯每天喝茶。', null, '/static/update/admin_audio/6E74B3D257304E77A2F859CF6AF50ACC.mp3', 'A：I see. People in Chengdu usually drink tea every day.', '0', '2018-10-25 17:51:35');
